import { makeStyles } from "tss-react/mui";
import colors from "../../../styles/color";

const useStyles = makeStyles()(() => ({
  background: {
    backgroundColor: colors.neutrals_7,
  },

  title: {
    fontFamily: "Montserrat !important",
    fontStyle: "normal",
    fontWeight: 700,
    fontSize: "40px",
    lineHeight: "56px",
    color: colors.neutrals_2,
    textAlign: "center",
  },

  mediaWrapper: {
    paddingTop: "40px",
    paddingBottom: "64px",
    display: "flex",
    gap: "24px",
  },

  mediaBox: {
    display: "flex",
    flexDirection: "column",
    backgroundColor: colors.white,
    height: "280px",
    marginBottom: "40px",
  },

  imageBox: {
    backgroundColor: colors.white,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    // height: "60%",
    // borderBottom: `1px solid ${colors.neutrals_6}`,
  },

  descriptionBox: {
    padding: "16px",
  },

  descriptionText: {
    fontFamily: "Montserrat !important",
    fontStyle: "normal",
    fontWeight: 600,
    fontSize: "16px",
    lineHeight: "22px",
    color: colors.neutrals_1,
  },
}));

export { useStyles };
