import { makeStyles } from 'tss-react/mui';
import colors from "../../../styles/color";

const useStyles = makeStyles()(() => ({
  footerContainer: {
    background: colors.neutrals_7,
    padding: "64px 120px",

    "@media (max-width: 1248px) and (min-width: 1024px)": {
      padding: "64px 80px",
    },

    "@media (max-width: 1024px)": {
      padding: "40px 24px",
    },
  },
  footerBox: {
    borderRadius: "16px",
    padding: "82px 100px",
    display: "flex",
    boxShadow: "0px 64px 64px -48px rgba(15, 15, 15, 0.08)",
    justifyContent: "space-between",
    maxWidth: "1440px",
    margin: "auto",

    "@media (max-width: 1024px)": {
      flexDirection: "column",
      padding: "40px 24px",
      gap: "32px",
    },
  },
  logoWrapper: {
    width: "16.67%",
    "@media (max-width: 1024px)": {
      width: "50%",
    },
  },
  footerTitle: {
    fontFamily: "Montserrat !important",
    fontWeight: 700,
    fontSize: "20px",
    lineHeight: "30px",
    color: colors.blackPearl,
    marginBottom: "32px",

    "@media (max-width: 1024px)": {
      fontSize: "16px",
      lineHeight: "120%",
      marginBottom: "24px",
    },
  },
  footerDesc: {
    fontFamily: "Montserrat !important",
    fontWeight: 500,
    fontSize: "16px",
    lineHeight: "140%",

    "@media (max-width: 1024px)": {
      fontSize: "14px",
    },
  },
  infoContainer: {
    display: "flex",
    flexDirection: "column",
    width: "25%",

    "@media (max-width: 1024px)": {
      width: "100%",
    },
  },
  contentContainer: {
    display: "flex",
    flexDirection: "column",
    gap: "16px",
  },
  descInfoContainer: {
    display: "flex",
    gap: "16px",
  },
  lastDescContainer: {
    display: "flex",
    justifyContent: "space-between",
  },
  descContainer: {
    display: "flex",
    flexDirection: "column",
  },
  copyRight: {
    marginTop: "32px",
    fontFamily: "Montserrat !important",
    fontWeight: 500,
    fontSize: "16px",
    lineHeight: "25px",
    textAlign: "center",

    "@media (max-width: 1024px)": {
      fontSize: "12px",
    },
  },
}));

export { useStyles };
