import FormControl from "@mui/material/FormControl";
import MenuItem from "@mui/material/MenuItem";
import Select from "@mui/material/Select";
import * as React from "react";

export default function Dropdownphone() {
  const [Phone, setPhone] = React.useState("");

  const handleChange = (event) => {
    setPhone(event.target.value);
  };

  return (
    <div>
      <FormControl sx={{ m: 1, minWidth: 80 }}>
        {/* <InputLabel id="dropdownphone">Phone</InputLabel> */}
        <Select
          labelId="dropdownphone"
          id="demo-simple-select-autowidth"
          value={Phone}
          onChange={handleChange}
          autoWidth
          label="Phone"
        >
          <MenuItem value="">Phone</MenuItem>
          <MenuItem value={1}>Iphone</MenuItem>
          <MenuItem value={2}>Samsung</MenuItem>
          <MenuItem value={3}>Xaomi</MenuItem>
        </Select>

        <TextField
          className={{
            root: {
              width: 200,
              "& .MuiOutlinedInput-input": {
                color: "green",
              },
              "& .MuiInputLabel-root": {
                color: "green",
              },
              "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
                borderColor: "green",
              },
              "&:hover .MuiOutlinedInput-input": {
                color: "red",
              },
              "&:hover .MuiInputLabel-root": {
                color: "red",
              },
              "&:hover .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                {
                  borderColor: "red",
                },
              "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-input": {
                color: "purple",
              },
              "& .MuiInputLabel-root.Mui-focused": {
                color: "purple",
              },
              "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                {
                  borderColor: "purple",
                },
            },
          }}
          value={age}
          onChange={(e) => setAge(e.target.value)}
          value={Phone}
          onChange={handleChange}
          variant="outlined"
          label="My Label"
          select
        >
          <MenuItem value="">Phone</MenuItem>
          <MenuItem value={1}>Iphone</MenuItem>
          <MenuItem value={2}>Samsung</MenuItem>
          <MenuItem value={3}>Xaomi</MenuItem>
        </TextField>
      </FormControl>
    </div>
  );
}
